import { Component } from '@angular/core';

@Component({
  selector: 'app-toast',
  templateUrl: './toast.component.html',
  styleUrls: ['./toast.component.css']
})
export class ToastComponent {
  public showToastFlag = false;
  public toastTitle: string = "";
  public toastMessage: string = "";
}
