import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class TimerService {

  constructor(private http: HttpClient) { }

  rootURL = 'https://localhost:7285/api';

  getTime() {
    return this.http.get<number>(`${this.rootURL}/Timer/Configure`);
  }

  stopTimer() {
    return this.http.post('https://localhost:7285/api/Timer/Stop', {})
  }

}
