import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-timer',
  templateUrl: './timer.component.html',
  styleUrls: ['./timer.component.css']
})

export class TimerComponent {

  timerConfig: any;
  countdown: number = 0;
  minutes: number = 0;
  seconds: number = 0;
  isTimerRunning: boolean = false;
  data: any = {};
  constructor(private http: HttpClient, private toastr: ToastrService) { }

  ngOnInit() {
    this.loadTimerConfiguration();
  }

  loadTimerConfiguration() {
    this.http.get<any>('https://localhost:7285/api/Timer/Data')
      .subscribe(
        (response) => {
          console.log("Response",response);
          this.data = response;
          console.log(this.data);
        },
        (error) => {
          this.toastr.error('Failed to load timer configuration.');
          console.error(error);
        }
      );
  }

  startTimer() {
    this.http.get<number>('https://localhost:7285/api/Timer/Configure')
      .subscribe(
        (response) => {
          this.timerConfig = response;
          this.isTimerRunning = true;
          this.countdown = response;
          this.toastr.success('Timer started.');

          this.updateCountdown();
        },
        (error) => {
          this.toastr.error('Failed to start timer.');
          console.error(error);
        }
      );
  }

  stopTimer() {
    this.http.post('https://localhost:7285/api/Timer/Stop', {})
      .subscribe(
        () => {
          this.isTimerRunning = false;
          this.toastr.success('Timer stopped.');
        },
        (error) => {
          this.toastr.error('Failed to stop timer.');
          console.error(error);
        }
      );
  }

  updateCountdown() {
    if (this.isTimerRunning && this.countdown > 0) {
      setTimeout(() => {
        this.countdown--;
        console.log(this.countdown);
        if(this.seconds>60){
          this.minutes = Math.floor(this.countdown/60);
          this.seconds = this.countdown - this.minutes*60;
          this.updateCountdown();
        }else{
          this.minutes = 0;
          this.seconds = this.countdown;
        }
      }, 1000);
    } else {
      this.toastr.success('Countdown completed.');
      //Tell BE that timer has stopped
      this.http.post('https://localhost:7285/api/Timer/Stop', {})
      .subscribe(
        () => {
          this.isTimerRunning = false;
          this.countdown = 0;
        },
        (error) => {
          this.toastr.error('Failed to stop timer.');
          console.error(error);
        }
      );
    }
  }
}
