import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-timer',
  templateUrl: './timer.component.html',
  styleUrls: ['./timer.component.css']
})
export class TimerComponent {
  timerConfig: any;
  countdown: number;
  isTimerRunning: boolean;

  constructor(private http: HttpClient, private toastr: ToastrService) { }

  ngOnInit() {
    this.loadTimerConfiguration();
  }

  loadTimerConfiguration() {
    this.http.get<any>('api/Timer/Configure')
      .subscribe(
        (response) => {
          this.timerConfig = response;
        },
        (error) => {
          this.toastr.error('Failed to load timer configuration.');
          console.error(error);
        }
      );
  }

  startTimer() {
    this.http.post('api/Timer/Start', {})
      .subscribe(
        () => {
          this.isTimerRunning = true;
          this.countdown = this.timerConfig.interval;
          this.toastr.success('Timer started.');
          this.updateCountdown();
        },
        (error) => {
          this.toastr.error('Failed to start timer.');
          console.error(error);
        }
      );
  }

  stopTimer() {
    this.http.post('api/Timer/Stop', {})
      .subscribe(
        () => {
          this.isTimerRunning = false;
          this.toastr.success('Timer stopped.');
        },
        (error) => {
          this.toastr.error('Failed to stop timer.');
          console.error(error);
        }
      );
  }

  updateCountdown() {
    if (this.countdown > 0) {
      setTimeout(() => {
        this.countdown--;
        this.updateCountdown();
      }, 1000);
    } else {
      this.toastr.success('Countdown completed.');
    }
  }
}
